public class Test {public void testRegisterMultipleClusterListener() { reset();  TestClusterListener clusterListener1 = new TestClusterListener(); _state.registerClusterListener(clusterListener1); TestClusterListener clusterListener2 = new TestClusterListener(); _state.registerClusterListener(clusterListener2); _state.listenToCluster(CLUSTER1_CLUSTER_NAME, new NullStateListenerCallback()); _state.listenToCluster(CLUSTER2_CLUSTER_NAME, new NullStateListenerCallback()); _clusterRegistry.put(CLUSTER1_CLUSTER_NAME, new ClusterProperties(CLUSTER1_CLUSTER_NAME)); _clusterRegistry.put(CLUSTER2_CLUSTER_NAME, new ClusterProperties(CLUSTER2_CLUSTER_NAME)); _clusterRegistry.put(CLUSTER2_CLUSTER_NAME, new ClusterProperties(CLUSTER2_CLUSTER_NAME)); assertEquals(clusterListener1.getCount(CLUSTER1_CLUSTER_NAME), 1, "expected 1 call"); assertEquals(clusterListener2.getCount(CLUSTER2_CLUSTER_NAME), 2, "expected 2 call");  }}